import React, { Component } from 'react';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';
// To use routing functionalities
import { Link } from 'react-router-dom';
import '../index.css';
import EmployeeService from './Services';

var divStyle = {
    margin: '8% 8%',
};

class ListEmployee extends Component {

    constructor(props) {
        super(props);
        this.employeeService = new EmployeeService();
        this.state = {
            employees: []
        }
        this.deleteEmployee = this.deleteEmployee.bind(this);
    }

    componentDidMount = () => {
        this.getEmployeeList();
    }

    // To get all the employees
    getEmployeeList() {
        axios.get('http://localhost:8000/employees')
            .then((response) => {
                console.log(response);
                this.setState({
                    employees: response.data
                });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    // To delete any employee
    deleteEmployee(empid) {
        this.employeeService.deleteEmployee('http://localhost:8000/employees/deleteEmployee/', empid);
        this.getEmployeeList();
    }

    render() {
        const { employees } = this.state;
        return (
            <div style={divStyle}>
                <Table responsive>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            employees && employees.map((employee, i) => {
                                return (
                                    <tr key={i}>
                                        <td>{i}</td>
                                        <td>{employee.firstName}</td>
                                        <td>{employee.lastName}</td>
                                        <td>{employee.email}</td>
                                        <td>{employee.phone}</td>
                                        <td>
                                            <Link to={"editemployee/" + employee._id} className="btn btn-primary">Edit</Link>
                                        </td>
                                        <td>
                                            <Button onClick={() => this.deleteEmployee(employee._id)} bsStyle="danger" >Delete</Button>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default ListEmployee;
